/*Table structure for table `tbl_substransaction` */

DROP TABLE IF EXISTS `tbl_substransaction`;

CREATE TABLE `tbl_substransaction` (
  `transactionid` varchar(50) NOT NULL,
  `transdate` datetime DEFAULT NULL,
  `useraction` int(11) NOT NULL,
  `transstatus` int(11) DEFAULT '-1',
  `productprice` double DEFAULT NULL,
  `chargeprice` double DEFAULT NULL,
  `mode` varchar(30) DEFAULT 'NA',
  `servicename` varchar(60) DEFAULT 'NA',
  `channelname` varchar(60) DEFAULT 'NA',
  `segment` varchar(50) DEFAULT 'NA',
  `updateon` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`transactionid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

